let currentLanguage = localStorage.getItem('language') || 'en';

function setLanguage(lang) {
    if (!translations[lang]) return;
    currentLanguage = lang;
    localStorage.setItem('language', lang);
    document.documentElement.lang = lang;
    updateContent();
}

function updateContent() {
    console.log('Updating content for language:', currentLanguage);
    
    // 翻译所有带有data-i18n属性的元素
    const elements = document.querySelectorAll('[data-i18n]');
    console.log('Found', elements.length, 'elements with data-i18n attribute');
    
    elements.forEach(element => {
        const key = element.getAttribute('data-i18n');
        console.log('Translating element with key:', key, 'Current text:', element.textContent);
        
        if (translations[currentLanguage][key]) {
            element.textContent = translations[currentLanguage][key];
            console.log('Translated to:', translations[currentLanguage][key]);
        } else {
            console.warn('No translation found for key:', key, 'in language:', currentLanguage);
        }
    });
    
    // 翻译所有带有data-i18n-placeholder属性的元素
    const placeholderElements = document.querySelectorAll('[data-i18n-placeholder]');
    console.log('Found', placeholderElements.length, 'elements with data-i18n-placeholder attribute');
    
    placeholderElements.forEach(element => {
        const key = element.getAttribute('data-i18n-placeholder');
        if (translations[currentLanguage][key]) {
            element.placeholder = translations[currentLanguage][key];
            console.log('Translated placeholder for key:', key, 'to:', translations[currentLanguage][key]);
        } else {
            console.warn('No translation found for placeholder key:', key, 'in language:', currentLanguage);
        }
    });
}

document.addEventListener('DOMContentLoaded', () => {
    const languageSelector = document.getElementById('language-selector');
    if (languageSelector) {
        languageSelector.value = currentLanguage;
        languageSelector.addEventListener('change', (e) => {
            setLanguage(e.target.value);
        });
    }
    updateContent();
}); 